import React, { useState } from 'react';
import { Container, Box, Typography, useTheme } from '@mui/material';
import InputPanel from './components/InputPanel';
import ResultPanel from './components/ResultPanel';
import Header from './components/Header';
import Footer from './components/Footer';
import LoadingOverlay from './components/LoadingOverlay';
import { processArticleClientSide } from './utils/clientSummarizer';
import axios from 'axios';

function App() {
  const theme = useTheme();
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [articleText, setArticleText] = useState('');
  const [activeModel, setActiveModel] = useState('client'); // 'client' or 'bart'

  const handleSubmit = async (text, model) => {
    if (!text.trim()) {
      setError('Please enter article text or URL.');
      return;
    }

    setArticleText(text);
    setLoading(true);
    setError(null);

    try {
      let resultData;

      if (model === 'client') {
        // Process using client-side algorithms
        resultData = processArticleClientSide(text);
        setTimeout(() => {
          setResult(resultData);
          setLoading(false);
        }, 1000); // Add slight delay for UX
      } else {
        // Process using server-side BART model
        const response = await axios.post('/api/summarize', {
          text: text,
          model_type: 'bart'
        });
        
        resultData = response.data;
        setResult(resultData);
        setLoading(false);
      }
    } catch (err) {
      console.error('Error processing article:', err);
      setError(err.response?.data?.error || 'An error occurred while processing the article.');
      setLoading(false);
    }
  };

  const handleModelChange = (model) => {
    setActiveModel(model);
  };

  return (
    <Box sx={{ 
      display: 'flex', 
      flexDirection: 'column', 
      minHeight: '100vh',
      bgcolor: theme.palette.background.default
    }}>
      <Header />
      
      <Container maxWidth="lg" sx={{ flex: 1, py: 4 }}>
        {error && (
          <Box sx={{ 
            p: 2, 
            mb: 3, 
            bgcolor: '#fee2e2', 
            color: '#b91c1c',
            borderRadius: 2
          }}>
            <Typography variant="body1">{error}</Typography>
          </Box>
        )}
        
        <InputPanel 
          onSubmit={handleSubmit} 
          activeModel={activeModel}
          onModelChange={handleModelChange}
        />
        
        {result && !loading && (
          <ResultPanel 
            summary={result.summary} 
            credibilityScore={result.credibilityScore} 
            credibilityFactors={result.credibilityFactors}
            articleText={articleText}
          />
        )}
      </Container>
      
      <Footer />
      
      {loading && <LoadingOverlay />}
    </Box>
  );
}

export default App;
